{
  "package_id": "file-org-phase5",
  "specialist": "project-janitor",
  "token_count": 2847,
  "priority": "critical",
  "coordination_metadata": {
    "parallel_safe": true,
    "dependencies": ["infrastructure"],
    "estimated_duration": "45 minutes",
    "rollback_capable": true
  },
  "mission": {
    "objective": "Organize 309 root files and consolidate 266 scattered files",
    "approach": "hybrid-refactor",
    "safety": "preserve all Docker and Python import paths"
  },
  "file_movement_patterns": {
    "root_to_organize": {
      "scripts/*.py": "scripts/",
      "test_*.py": "tests/",
      "validate_*.py": "tests/validation/",
      "monitor_*.sh": "scripts/monitoring/",
      "*_profiler.py": "scripts/profiling/",
      "deploy_*.py": "scripts/deployment/",
      "cognitive_*.json": "data/cognitive/"
    },
    "shared_consolidation": {
      "app/shared/utils/*.py": "shared/utils/",
      "app/shared/services/*.py": "shared/services/",
      "app/shared/models/*.py": "shared/models/"
    }
  },
  "cleanup_targets": {
    "__pycache__": {
      "count": 326,
      "action": "recursive_delete",
      "command": "find . -type d -name __pycache__ -exec rm -rf {} +"
    },
    "backup_files": {
      "patterns": ["*.backup.*", "*.bak", "*~"],
      "count": 21,
      "action": "archive_then_delete"
    },
    "temp_files": {
      "patterns": ["*.tmp", "temp_*", "test_*_temp"],
      "count": 126,
      "action": "review_then_delete"
    }
  },
  "deduplication": {
    "duplicate_groups": 12,
    "total_duplicates": 35,
    "strategy": "keep_most_recent",
    "verification": "md5sum comparison"
  },
  "safety_procedures": {
    "pre_checks": [
      "backup critical files",
      "verify Docker compose paths",
      "test Python imports",
      "create rollback snapshot"
    ],
    "validation": [
      "docker-compose config validation",
      "python import verification",
      "service health checks"
    ],
    "rollback": {
      "trigger": "any service failure",
      "method": "git reset --hard",
      "backup_location": ".claude/backups/pre-organization/"
    }
  },
  "success_criteria": {
    "files_organized": "309 root files moved to appropriate directories",
    "shared_consolidated": "266 scattered files consolidated",
    "cleanup_complete": "473 junk files removed",
    "services_operational": "all Docker services healthy",
    "imports_valid": "no Python import errors"
  }
}
