{
  "package_type": "infrastructure_integration",
  "priority": "high",
  "token_limit": 3500,
  "synthesis_timestamp": "2025-08-15T10:31:00Z",

  "integration_architecture": {
    "service_mesh": {
      "network": "ai_workflow_engine_net",
      "subnet": "172.100.0.0/24",
      "cognitive_service_ips": {
        "text-to-speech": "172.100.0.40",
        "voice-to-text": "172.100.0.41",
        "voice-cloning": "172.100.0.42",
        "text-generation": "172.100.0.43",
        "llm-assistant": "172.100.0.44",
        "image-generation": "172.100.0.45"
      }
    },

    "api_gateway_routes": {
      "/api/v1/cognitive/tts": "text-to-speech-service:8040",
      "/api/v1/cognitive/stt": "voice-to-text-service:8041",
      "/api/v1/cognitive/voice-clone": "voice-cloning-service:8042",
      "/api/v1/cognitive/text-gen": "advanced-text-generation-service:8043",
      "/api/v1/cognitive/assistant": "llm-assistant-service:8044",
      "/api/v1/cognitive/image-gen": "image-generation-service:8045"
    },

    "database_connections": {
      "postgresql": {
        "host": "postgresql",
        "port": 5432,
        "database": "cognitive_services",
        "pooling": "pgbouncer",
        "max_connections": 20
      },
      "redis": {
        "host": "redis",
        "port": 6379,
        "databases": {
          "cache": 0,
          "sessions": 1,
          "cognitive_results": 2
        }
      },
      "qdrant": {
        "host": "qdrant",
        "port": 6333,
        "collections": ["embeddings", "voice_profiles", "generated_content"]
      }
    },

    "monitoring_integration": {
      "prometheus_endpoints": [
        "/metrics",
        "/api/metrics"
      ],
      "grafana_dashboards": [
        "cognitive-services-overview",
        "tts-performance",
        "stt-accuracy"
      ],
      "alerting_rules": {
        "service_down": "up{job='cognitive-*'} == 0",
        "high_latency": "http_request_duration_seconds > 2",
        "error_rate": "rate(http_requests_total{status=~'5..'}[5m]) > 0.05"
      }
    }
  },

  "fallback_mechanisms": {
    "service_unavailable": {
      "strategy": "graceful_degradation",
      "response": {
        "status": 503,
        "message": "Cognitive service temporarily unavailable",
        "fallback_url": "/api/v1/basic/{service}"
      }
    },
    "circuit_breaker": {
      "failure_threshold": 5,
      "timeout": "30s",
      "half_open_requests": 3
    }
  },

  "security_configuration": {
    "authentication": "JWT tokens from main API",
    "rate_limiting": "100 requests per minute per user",
    "cors_origins": ["http://localhost:3030", "https://aiwfe.com"],
    "ssl_termination": "nginx-proxy"
  }
}
