{
    "package_type": "INFRASTRUCTURE_MONITORING",
    "priority": "HIGH",
    "token_limit": 3000,
    "deployment_strategy": {
        "tools": ["Prometheus", "Grafana"],
        "container_management": "Docker Compose",
        "network_configuration": "Isolated monitoring network"
    },
    "monitoring_objectives": [
        "Real-time system health tracking",
        "Automated alerting for critical metrics",
        "Comprehensive dashboard visualization"
    ],
    "alert_configuration": {
        "critical_thresholds": [
            "CPU Usage > 80%",
            "Memory Utilization > 85%", 
            "Disk I/O Saturation",
            "Network Latency Spikes"
        ],
        "notification_channels": [
            "Email",
            "Slack Integration",
            "PagerDuty Escalation"
        ]
    },
    "dashboard_components": [
        "System Resource Utilization",
        "Service Health Status", 
        "Request Latency Heatmap",
        "Error Rate Tracking"
    ],
    "health_check_integration": {
        "endpoint_monitoring": "/health",
        "service_dependency_tracking": true,
        "automatic_recovery_attempts": true
    }
}
