{
    "package_type": "PERFORMANCE_OPTIMIZATION",
    "priority": "MEDIUM",
    "token_limit": 4000,
    "optimization_targets": [
        "Galaxy Animation Frame Management",
        "WebGL Context Performance",
        "Rendering Pipeline Efficiency"
    ],
    "performance_strategies": {
        "frame_management": {
            "techniques": [
                "Dynamic Frame Skipping",
                "Adaptive Quality Reduction",
                "Progressive Rendering"
            ],
            "implementation_approach": {
                "detection_mechanism": "Real-time performance monitoring",
                "quality_scaling": {
                    "min_threshold": 30,
                    "max_threshold": 60,
                    "default_fps": 45
                }
            }
        },
        "webgl_optimization": {
            "context_recovery": {
                "automatic_reset_strategy": true,
                "fallback_rendering_modes": [
                    "Software Rendering",
                    "Low-Resolution Mode"
                ]
            },
            "memory_management": {
                "buffer_cleanup_frequency": "Per Frame",
                "aggressive_garbage_collection": true
            }
        }
    },
    "monitoring_integration": {
        "performance_metrics": [
            "Frames Per Second",
            "GPU Utilization",
            "Memory Consumption",
            "Render Time"
        ],
        "alerting_thresholds": {
            "fps_drop": 20,
            "render_time_spike": 100,
            "memory_leak": "50MB/minute"
        }
    },
    "fallback_mechanisms": {
        "performance_degradation_handling": [
            "Automatic Quality Reduction",
            "Frame Rate Stabilization",
            "Partial Rendering Suspension"
        ]
    }
}
