content:
{
    "package_type": "TESTING_FRAMEWORK",
    "priority": "HIGH",
    "token_limit": 3500,
    "testing_objectives": [
        "Comprehensive Cross-Browser Validation",
        "Automated Evidence Collection",
        "Unified Testing Strategy"
    ],
    "framework_configuration": {
        "primary_tool": "Playwright",
        "browser_coverage": [
            "Chrome",
            "Firefox", 
            "Safari",
            "Edge"
        ],
        "testing_modes": [
            "Headless",
            "Headed",
            "Mobile Emulation"
        ]
    },
    "validation_strategies": {
        "interaction_testing": {
            "core_scenarios": [
                "User Authentication Flow",
                "Critical Path Navigation",
                "Form Submission",
                "Dynamic Content Rendering"
            ],
            "validation_checks": [
                "UI Responsiveness",
                "Element Visibility",
                "Interaction Success Rate",
                "Error Handling"
            ]
        },
        "evidence_collection": {
            "capture_modes": [
                "Screenshots",
                "Video Recording", 
                "Performance Metrics",
                "Console Logs"
            ],
            "storage_strategy": {
                "retention_period": "30 days",
                "compression_level": "high",
                "backup_frequency": "After Each Test Suite"
            }
        }
    },
    "reporting_mechanism": {
        "output_format": [
            "JSON",
            "HTML",
            "CI/CD Compatible Artifacts"
        ],
        "metrics_tracked": [
            "Test Coverage Percentage",
            "Failure Rate",
            "Average Test Duration",
            "Critical Path Success Rate"
        ]
    },
    "integration_points": {
        "ci_cd_compatibility": [
            "GitHub Actions",
            "Jenkins",
            "GitLab CI"
        ],
        "automated_deployment_triggers": {
            "on_test_success": true,
            "rollback_on_failure": true
        }
    }
}
