{
  "package_type": "production_validation",
  "priority": "CRITICAL",
  "token_limit": 4000,
  "created_at": "2025-08-15T21:10:00Z",
  "iteration": 5,
  "phase": 4,

  "production_endpoints": {
    "primary": "https://aiwfe.com",
    "secondary": "http://aiwfe.com",
    "cloudflare_protected": true,
    "ssl_certificate": "Valid through Cloudflare"
  },

  "user_experience_validation": {
    "playwright_automation": {
      "test_suite": "Production smoke tests",
      "required_evidence": [
        "Screenshots of key pages",
        "Navigation flow completion",
        "Form submission success",
        "Authentication workflow",
        "Error handling verification"
      ]
    },

    "critical_user_journeys": [
      {
        "journey": "User Registration",
        "steps": [
          "Navigate to https://aiwfe.com",
          "Click register button",
          "Fill registration form",
          "Submit and verify success",
          "Check welcome message"
        ]
      },
      {
        "journey": "User Login",
        "steps": [
          "Navigate to login page",
          "Enter credentials",
          "Submit login form",
          "Verify dashboard access",
          "Check user profile"
        ]
      },
      {
        "journey": "Core Functionality",
        "steps": [
          "Access main features",
          "Interact with AI services",
          "Submit workflow request",
          "Verify processing",
          "Check results display"
        ]
      }
    ]
  },

  "evidence_collection_requirements": {
    "mandatory_proof": [
      "curl https://aiwfe.com with response",
      "Browser screenshot of landing page",
      "Network tab showing successful requests",
      "Console logs showing no errors",
      "Lighthouse/performance metrics"
    ],

    "validation_commands": [
      "curl -I https://aiwfe.com",
      "curl -X GET https://aiwfe.com/api/health",
      "nslookup aiwfe.com",
      "openssl s_client -connect aiwfe.com:443"
    ]
  },

  "performance_validation": {
    "metrics": [
      "Page load time < 3 seconds",
      "API response time < 500ms",
      "No JavaScript errors",
      "All resources loading successfully",
      "Mobile responsiveness verified"
    ],

    "monitoring": {
      "uptime": "Verify 100% uptime during testing",
      "response_codes": "All 200/201, no 4xx/5xx errors",
      "ssl_validation": "HTTPS enforced, valid certificate",
      "cdn_performance": "Cloudflare caching working"
    }
  },

  "rollback_triggers": {
    "critical_failures": [
      "Site not accessible",
      "Authentication broken",
      "Database connectivity lost",
      "Core features not working",
      "Security vulnerabilities detected"
    ],

    "rollback_procedure": {
      "step_1": "Identify failure point",
      "step_2": "Revert configuration changes",
      "step_3": "Restore from backup if needed",
      "step_4": "Validate restoration",
      "step_5": "Document incident"
    }
  }
}
